# Tokenize documents
tokenized_documents = tokenizer(
    documents, return_tensors='pt', padding=True, truncation=True, max_length=512)

# Convert tokenized documents to a serializable format
tokenized_documents_dict = {key: value.tolist()
                            for key, value in tokenized_documents.items()}

# Save tokenized documents to a file
with open('tokenized_documents.json', 'w', encoding='utf-8') as f:
    json.dump(tokenized_documents_dict, f, ensure_ascii=False, indent=4)

# Get BERT embeddings for documents
with torch.no_grad():
    document_hidden_states = model(**tokenized_documents).last_hidden_state
    document_embeddings = torch.max(document_hidden_states, dim=1)[
        0].cpu().numpy()  # Max pooling

# Save document embeddings to a file
with open('document_embeddings.json', 'w', encoding='utf-8') as f:
    json.dump(document_embeddings.tolist(), f, ensure_ascii=False, indent=4)

# Normalize the document embeddings for cosine similarity
faiss.normalize_L2(document_embeddings)

# Save normalized document embeddings to a file
with open('normalized_document_embeddings.json', 'w', encoding='utf-8') as f:
    json.dump(document_embeddings.tolist(), f, ensure_ascii=False, indent=4)

# Initialize FAISS index
dimension = document_embeddings.shape[1]
# Inner Product is equivalent to cosine similarity for normalized vectors
index = faiss.IndexFlatIP(dimension)
index.add(document_embeddings)


@app.route('/search', methods=['POST'])
def search():
    query = request.json.get('query')

    # Tokenize and encode the query
    tokenized_query = tokenizer(
        query, return_tensors='pt', padding=True, truncation=True, max_length=512)

    # Get BERT embeddings for the query
    with torch.no_grad():
        query_hidden_states = model(**tokenized_query).last_hidden_state
        query_embedding = torch.max(query_hidden_states, dim=1)[
            0].cpu().numpy()

    # Normalize query embedding for cosine similarity
    faiss.normalize_L2(query_embedding)

    # Search for the nearest neighbors
    _, indices = index.search(query_embedding, 1)

    # Get the top-ranked document index
    top_document_index = indices[0][0]

    print("the question is "+ documents[top_document_index])
    print("the answer is "+ answers[top_document_index])

    # Return the most similar document
    response = {'document': answers[top_document_index]}
    return jsonify(response)  # Use jsonify to handle JSON encoding


